from PIL import Image, ImageDraw, ImageFilter; import random; width = 1920; height = 1080; img = Image.new("RGB", (width, height), (18, 18, 18)); draw = ImageDraw.Draw(img); [draw.rectangle([x, y, x+39, y+39], outline=(34, 197, 94, 20)) for x in range(0, width, 40) for y in range(0, height, 40)]; [(lambda x, y, s: draw.ellipse([x-s, y-s, x+s, y+s], fill=(34, 197, 94, 20)))(random.randint(300, width-300), random.randint(300, height-300), random.randint(100, 300)) for _ in range(10)]; img = img.filter(ImageFilter.GaussianBlur(radius=2)); img.save("public/images/albuquerque-map.jpg", quality=95)
